import axios from "axios";
import {
  NOTES_CREATE_FAIL,
  NOTES_CREATE_REQUEST,
  NOTES_CREATE_SUCCESS,
  NOTES_LIST_FAIL,
  NOTES_LIST_REQUEST,
  NOTES_LIST_SUCCESS,
} from "../constants/notesConstatns";

export const listNotes = () => async (dispatch, getState) => {
  try {
    dispatch({
      //in dispatch we are disparching the action NOTES_LIST_REQUEST, its going to send loading to true
      type: NOTES_LIST_REQUEST,
    });

    //fetching user info from the state with getState actions
    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.get(`/api/notes`, config);

    dispatch({
      type: NOTES_LIST_SUCCESS,
      payload: data,
    });
  } catch (error) {
    const message =
      error.response && error.response.data.message
        ? error.response.data.message
        : error.message;
    dispatch({
      type: NOTES_LIST_FAIL,
      payload: message,
    });
  }
};

//4. action for creating note

export const createNoteAction =
  (title, content, category) => async (dispatch, getState) => {
    try {
      dispatch({
        type: NOTES_CREATE_REQUEST,
      });

      const {
        userLogin: { userInfo },
      } = getState();

      const config = {
        headers: {
          //for what type of contetn we are sending the data.. JSON file
          "Content-Type": "application/json",
          //authorization we make with bearer token
          Authorization: `Bearer ${userInfo.token}`,
        },
      };

      //we are making api call from /api/notes/create, we are sending title, conent, category that we recived from the user to the backend then we are passing config object wich created on line 60.
      const { data } = await axios.post(
        `/api/notes/create`,
        { title, content, category },
        config
      );

      //in this area if const {data} is succes if it doesnt fail it going to fire of NOTES CREATE SUCCESS its going to pass data to payload
      dispatch({
        type: NOTES_CREATE_SUCCESS,
        payload: data,
      });

      //otherwise its goint to throw a error
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message;
      dispatch({
        type: NOTES_CREATE_FAIL,
        payload: message,
      });
    }
  };
